<script>
            $( document ).ready(function(event) {
                $("#sum_slider").ionRangeSlider({
                    min: Math.round(0 / parseFloat($("input[name='type_id']:checked").data('price_coin'))),
                    max: 10000,
                    from: Math.round(0 / parseFloat($("input[name='type_id']:checked").data('price_coin'))),
                    grid: true,
                    postfix: ' CoL',
                    skin: 'round',
                    prettify: function (num) {
                        let bonus = 0;
                        let bonus_item = [];
                        let bonus_item_key = [];
                        let bonus_item_html = '<div class="row border-bottom text-center pt-5" style="border-bottom-style: dashed !important;"><div class="col-12">Gift items</div></div>';
                        let bonus_item_show = false;
                        let payment_method = $("input[name='payment_method']:checked").val();
                        let price = parseFloat($("input[name='type_id']:checked").data('price'));
                        let temp_agrigator_349 = ["all"];
                        if (temp_agrigator_349.includes(payment_method) || temp_agrigator_349.includes('all')) {

                            //Скрывааем все блоки итемов
                            $('.ditems').hide();
                            $('#donat_items').val('');
                            $('#bonus-items-label').hide();

                                                                                                                                                                                                                                
                                        if (Math.floor(num * price) >= 100 && Math.floor(num * price) <= 101) {
                                            bonus += (Math.floor(num * price) * 10 / 100);
                                            $('#donat_items').val($('#inpitems_1').val());
                                            $('.ditems').hide();

                                            let its = $('#ditems_1').find('.donate-item-val-block');
                                            if (its.length) {
                                                $('#ditems_1').show();
                                                $('#bonus-items-label').show();
                                            }
